mapper.select:
SELECT id, C_DistrictBCDId, C_AddressId, C_ChannelNum, C_HardwareVer, C_SoftwareVer, C_FixIP, C_LastComTime, C_SoftUpdateDate, C_InstallDate, C_Desp, nbDeviceId FROM t_bdtu where 1=1
and LOWER(C_DistrictBCDId) like LOWER(CONCAT("%",#{cDistrictbcdid,jdbcType=VARCHAR},"%"))
and LOWER(C_AddressId) like LOWER(CONCAT("%",#{cAddressid,jdbcType=VARCHAR},"%"))
and C_InstallDate >=#{cInstalldateBegin,jdbcType=VARCHAR}
and C_InstallDate <=#{cInstalldateEnd,jdbcType=VARCHAR}
order by id desc

SELECT count(id) FROM t_bdtu
where 1=1
and LOWER(C_DistrictBCDId) like LOWER(CONCAT("%",#{cDistrictbcdid,jdbcType=VARCHAR},"%"))
and LOWER(C_AddressId) like LOWER(CONCAT("%",#{cAddressid,jdbcType=VARCHAR},"%"))
and C_InstallDate >=#{cInstalldateBegin,jdbcType=VARCHAR}
and C_InstallDate <=#{cInstalldateEnd,jdbcType=VARCHAR}

mapper.insert:

insert into t_bdtu (C_DistrictBCDId, C_AddressId, 
  C_ChannelNum, C_HardwareVer, C_SoftwareVer, 
  C_FixIP, C_LastComTime, C_SoftUpdateDate, 
  C_InstallDate, C_Desp, nbDeviceId
  )
values ( #{cDistrictbcdid,jdbcType=VARCHAR}, #{cAddressid,jdbcType=VARCHAR}, 
  #{cChannelnum,jdbcType=INTEGER}, #{cHardwarever,jdbcType=VARCHAR}, #{cSoftwarever,jdbcType=VARCHAR}, 
  #{cFixip,jdbcType=VARCHAR}, #{cLastcomtime,jdbcType=VARCHAR}, #{cSoftupdatedate,jdbcType=VARCHAR}, 
  #{cInstalldate,jdbcType=VARCHAR}, #{cDesp,jdbcType=VARCHAR}, #{nbdeviceid,jdbcType=VARCHAR}
  )
