mapper.select:
select 

from t_deviceinfo
where C_DistrictBCDId = #{cDistrictbcdid,jdbcType=VARCHAR}
  and C_AddressId = #{cAddressid,jdbcType=INTEGER}

mapper.delete:
delete from t_deviceinfo
where C_DistrictBCDId = #{cDistrictbcdid,jdbcType=VARCHAR}
  and C_AddressId = #{cAddressid,jdbcType=INTEGER}
