mapper.select:
SELECT am.meterId as row_id, am.meterBoxId as epu_id, am.meter_name as ammeter_name, am.meter_no as ammeter_id, am.house_id, am.phase_remark, am.CREATE_TIME, am.CREATE_ID, am.UPDATE_TIME, am.UPDATE_ID, am.DEL_FLAG, am.C_DistrictBCDId as district_id, am.C_AddressId as address_id, am.C_ChannelNum as channel_id, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_name, epu_province_name, epu_city_name, epu_district_name FROM t_meter am LEFT JOIN view_epu_info epu ON am.meterBoxId = epu.row_id where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and epu.epu_province= #{epuProvince,jdbcType=VARCHAR}
and epu.epu_city= #{epuCity,jdbcType=VARCHAR}
and epu.epu_district= #{epuDistrict,jdbcType=VARCHAR}
and LOWER(am.meter_name) like LOWER(CONCAT("%",#{ammeterName,jdbcType=VARCHAR},"%"))
and ( LOWER(am.C_DistrictBCDId) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.C_AddressId) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.C_ChannelNum) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) )
order by am.CREATE_TIME desc

SELECT count(am.meterId) FROM t_meter am LEFT JOIN view_epu_info epu ON am.meterBoxId = epu.row_id where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and epu.epu_province= #{epuProvince,jdbcType=VARCHAR}
and epu.epu_city= #{epuCity,jdbcType=VARCHAR}
and epu.epu_district= #{epuDistrict,jdbcType=VARCHAR}
and LOWER(am.meter_name) like LOWER(CONCAT("%",#{ammeterName,jdbcType=VARCHAR},"%"))
and ( LOWER(am.C_DistrictBCDId) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.C_AddressId) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.C_ChannelNum) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) )

SELECT am.meterId as row_id, am.meterBoxId as epu_id, am.meter_name as ammeter_name, am.meter_no as ammeter_id, am.house_id, am.phase_remark, am.CREATE_TIME, am.CREATE_ID, am.UPDATE_TIME, am.UPDATE_ID, am.DEL_FLAG, am.C_DistrictBCDId as district_id, am.C_AddressId as address_id, am.C_ChannelNum as channel_id, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_name, 'M0005' as epu_type, epu.epu_province_name, epu.epu_city_name, epu.epu_district_name FROM t_meter am LEFT JOIN view_epu_info epu ON am.meterBoxId = epu.row_id where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and am.meterId = #{rowId,jdbcType=VARCHAR}
and am.meterBoxId = #{epuId,jdbcType=VARCHAR}
order by am.UPDATE_TIME desc
