mapper.select:
SELECT epu.row_id, epu.district_id, epu.address_id, epu.epu_name, epu.epu_local, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_type, epu.epu_xscale, epu.epu_yscale, epu.epu_parent_id, epu.CREATE_TIME, epu.CREATE_ID, epu.UPDATE_TIME, epu.UPDATE_ID, epu.DEL_FLAG, epu.channel_id, epu.channel_id_two, epu.channel_id_three, epu.line_id, epu.epu_province_name, epu.epu_city_name, epu.epu_district_name, epu.epu_type_name, epu.epu_parent_name, epu.epu_parent_type, epu.epu_parent_type_name FROM view_epu_info epu where 1=1 and epu.DEL_FLAG='0'
and epu.row_id = #{rowId,jdbcType=VARCHAR}
order by epu.UPDATE_TIME desc

SELECT epu.row_id, epu.district_id, epu.address_id, epu.epu_name, epu.epu_local, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_type, epu.epu_xscale, epu.epu_yscale, epu.epu_parent_id, epu.CREATE_TIME, epu.CREATE_ID, epu.UPDATE_TIME, epu.UPDATE_ID, epu.DEL_FLAG, epu.channel_id, epu.channel_id_two, epu.channel_id_three, epu.line_id, epu.epu_province_name, epu_city_name, epu_district_name, epu_type_name, epu_parent_name, epu_parent_type, epu_parent_type_name FROM view_epu_info epu where 1=1 and epu.DEL_FLAG='0' and epu.epu_type='M0001' and epu.epu_xscale IS NOT NULL and epu.epu_Yscale IS NOT NULL
and epu.row_id = #{rowId,jdbcType=VARCHAR}
and epu.epu_city = #{epuCity,jdbcType=VARCHAR}
and epu.epu_district = #{epuDistrict,jdbcType=VARCHAR}

SELECT epu.row_id, epu.district_id, epu.address_id, epu.epu_name, epu.epu_local, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_type, epu.epu_xscale, epu.epu_yscale, epu.epu_parent_id, epu.CREATE_TIME, epu.CREATE_ID, epu.UPDATE_TIME, epu.UPDATE_ID, epu.DEL_FLAG, epu.channel_id, epu.channel_id_two, epu.channel_id_three, epu.line_id, epu_province_name, epu_city_name, epu_district_name, epu_type_name, epu_parent_name, epu_parent_type, epu_parent_type_name FROM view_epu_info epu where 1=1 and epu.DEL_FLAG='0'
and epu.row_id = #{rowId,jdbcType=VARCHAR}
and FIND_IN_SET(epu.row_id,getEpuChildrenList(#{epuParentId,jdbcType=VARCHAR}))
and epu.epu_province=#{epuProvince,jdbcType=VARCHAR} and epu.epu_type=#{epuType,jdbcType=VARCHAR}
and epu.epu_city=#{epuCity,jdbcType=VARCHAR} and epu.epu_type=#{epuType,jdbcType=VARCHAR}
and epu.epu_district=#{epuDistrict,jdbcType=VARCHAR} and epu.epu_type=#{epuType,jdbcType=VARCHAR}
order by epu.UPDATE_TIME desc

SELECT epu.row_id, epu.district_id, epu.address_id, epu.epu_name, epu.epu_local, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_type, epu.epu_xscale, epu.epu_yscale, epu.epu_parent_id, epu.CREATE_TIME, epu.CREATE_ID, epu.UPDATE_TIME, epu.UPDATE_ID, epu.DEL_FLAG, epu.channel_id, epu.channel_id_two, epu.channel_id_three, epu.line_id, epu_province_name, epu_city_name, epu_district_name, epu_type_name, epu_parent_name, epu_parent_type, epu_parent_type_name FROM view_epu_info epu where 1=1 and epu.DEL_FLAG='0'
and epu.epu_province= #{epuProvince,jdbcType=VARCHAR}
and epu.epu_city= #{epuCity,jdbcType=VARCHAR}
and epu.epu_district= #{epuDistrict,jdbcType=VARCHAR}
and epu.epu_type= #{epuType,jdbcType=VARCHAR}
and LOWER(epu.epu_name) like LOWER(CONCAT("%",#{epuName,jdbcType=VARCHAR},"%"))
and ( LOWER(epu.district_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(epu.address_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(epu.channel_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(epu.channel_id_two) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(epu.channel_id_three) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) )
order by epu.UPDATE_TIME desc

